informalexample.programlisting:
#!/usr/bin/perl -w


use strict;

use warnings;


use SOAP::Lite;

use Data::Dumper;


my $soap = SOAP::Lite

->  uri('urn:Atomia::DNS::Server')

->  proxy('http://your.soap.server/atomiadns')

->  on_fault(sub {

my($soap, $res) = @_;

die "got fault of type " . $res->faultcode . ": " . (ref $res ? $res->faultstring : $soap->transport->status) . "\n";

});


my $res;

$res = $soap->AddZone('example.se', 3600, 'ns1.somecompany.se.', 'registry.somecompany.se.', 10800, 3600, 604800, 86400, [ 'ns1.somecompany.se', 'ns2.somecompany.se' ], 'nameservergroup');


print "AddZone returned " . Dumper($res);


$res = $soap->AddDnsRecords('example.se', [SOAP::Data->new(name => 'resourcerecord', value => { label => '@', class => 'IN', ttl => 3600, type => 'A', rdata => '127.0.0.1' }),

SOAP::Data->new(name => 'resourcerecord', value => { label => '@', class => 'IN', ttl => 3600, type => 'AAAA', rdata => '2001:0DB8::1' }),

SOAP::Data->new(name => 'resourcerecord', value => { label => 'mail', class => 'IN', ttl => 3600, type => 'AAAA', rdata => '2001:0DB8::2' }),

SOAP::Data->new(name => 'resourcerecord', value => { label => '@', class => 'IN', ttl => 3600, type => 'MX', rdata => 'mail.example.se.' })

]);


print "AddDnsRecords returned " . Dumper($res);

using System;

using System.Collections.Generic;

using System.Text;

using AtomiaDNSExampleClient.AtomiaDNS;


namespace AtomiaDNSExampleClient

{

class Program

{

static void Main(string[] args)

{

AtomiaDNSService ws = new AtomiaDNSService();

ws.Url = "http://your.soap.server.installation/atomiadns";


try

{

DeleteZone zone = new DeleteZone();

zone.zonename = "testzone-fromdotnet.com";


DeleteZoneResponse response = ws.DeleteZone(zone);

Console.WriteLine("Zone deleted, got status " + response.status);

}

catch (Exception e)

{

Console.WriteLine("Caught exception when removing zone: " + e);

}


try

{

AddZone zone = new AddZone();

zone.mname = "ns1.atomiadns.com.";

zone.refresh = 10800;

zone.retry = 3600;

zone.expire = 604800;

zone.minimum = 3600;

zone.zonettl = 60;

zone.zonename = "testzone-fromdotnet.com";

zone.rname = "registry.atomiadns.com.";

zone.nameservers = new string[] { "ns1.atomiadns.com.", "ns2.atomiadns.com." };

zone.nameservergroup = "someservergroup";

AddZoneResponse response = ws.AddZone(zone);


Console.WriteLine("Zone added, got status " + response.status);

}

catch (Exception e)

{

Console.WriteLine("Caught exception when adding zone: " + e);

}

}

}

}

section.para:
The Atomia DNS Nameserver has been tested using dnsperf (
http://www.nominum.com/services/measurement_tools.php
) on a modest system to see its performance.
